package com.example.huangweiliang.mortgagecalculator;import android.content.Context;import android.support.annotation.Nullable;import android.util.AttributeSet;import android.widget.LinearLayout;public class KeyBoardView extends LinearLayout {    public KeyBoardView(Context context) {        super(context);        initView();    }    public KeyBoardView(Context context, @Nullable AttributeSet attrs) {        super(context, attrs);        initView();    }    public KeyBoardView(Context context, @Nullable AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        initView();    }    private void initView() {        inflate(getContext(),R.layout.key_board_view,this);    }}