package com.example.huangweiliang.mortgagecalculator.activity;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.ImageButton;import android.widget.TextView;import com.example.huangweiliang.mortgagecalculator.R;import com.example.huangweiliang.mortgagecalculator.model.MortgageData;import com.example.huangweiliang.mortgagecalculator.model.ResultData;import com.example.huangweiliang.mortgagecalculator.view.HFViewAdapter;import com.example.huangweiliang.mortgagecalculator.view.ResultBottomView;import com.example.huangweiliang.mortgagecalculator.view.ResultDetailAdapter;import com.example.huangweiliang.mortgagecalculator.view.ResultTopView;public class MorResultActivity extends AppCompatActivity {    private RecyclerView resultDetailList;    private ResultDetailAdapter resultDetailAdapter;    private HFViewAdapter hfViewAdapter;    private ResultTopView headerView ;    private ResultBottomView footerView ;    private ImageButton back;    private TextView detail ;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        getWindow().setStatusBarColor(getResources().getColor(R.color.titile_bg_color));        setContentView(R.layout.activity_mor_result);        initView();        ResultData[][] resultDatas = (ResultData[][]) getIntent().getSerializableExtra(MortgageData.RESULTDATA);        if (resultDatas != null) {            hfViewAdapter.setResultDatas(resultDatas);            headerView.setData(resultDatas);        }    }    private void initView() {        resultDetailList = (RecyclerView) findViewById(R.id.result_detail_list);        LinearLayoutManager layoutManager = new LinearLayoutManager(this);        resultDetailList.setLayoutManager(layoutManager);        resultDetailAdapter = new ResultDetailAdapter();        hfViewAdapter = new HFViewAdapter(resultDetailAdapter);        headerView = new ResultTopView(this);        footerView = new ResultBottomView(this);        hfViewAdapter.addHeaderView(headerView);        hfViewAdapter.addFooterView(footerView);        resultDetailList.setAdapter(hfViewAdapter);        back = (ImageButton) findViewById(R.id.back);        back.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                finish();            }        });        detail = (TextView) findViewById(R.id.detail);        detail.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                resultDetailList.scrollToPosition(0);            }        });    }}