package com.example.huangweiliang.mortgagecalculator.view;import android.graphics.Color;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.LinearLayout;import android.widget.TextView;import com.example.huangweiliang.mortgagecalculator.R;import com.example.huangweiliang.mortgagecalculator.model.ResultData;import java.math.BigDecimal;public class ResultDetailAdapter extends RecyclerView.Adapter<ResultDetailAdapter.ResultDetailViewHolder> {    private ResultData[][] resultDatas;    public void setResultDatas(ResultData[][] resultDatas) {        this.resultDatas = resultDatas;    }    @Override    public ResultDetailViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.result_detail_item,parent,false);        return new ResultDetailViewHolder(view);    }    @Override    public void onBindViewHolder(ResultDetailViewHolder holder, int position) {        if (position == 0) {            holder.itemData.setBackgroundColor(Color.parseColor("#dcdcdc"));            holder.itemData.setVisibility(View.VISIBLE);            holder.repayment.setText("期次");            holder.pay.setText("偿还本息");            holder.principal.setText("偿还本金");            holder.interest.setText("支付利息");            holder.residualLoan.setText("剩余本金");        } else {            holder.itemData.setBackgroundColor(Color.WHITE);            holder.itemData.setVisibility(View.VISIBLE);            holder.repayment.setText(String.valueOf(position));            holder.pay.setText(String.format("%.2f",(resultDatas[0][position - 1].getPay()                    + resultDatas[1][position - 1].getPay())));            holder.principal.setText(String.format("%.2f",(resultDatas[0][position - 1].getPrincipal()                    + resultDatas[1][position - 1].getPrincipal())));            holder.interest.setText(String.format("%.2f",(resultDatas[0][position - 1].getInterest()                    + resultDatas[1][position - 1].getInterest())));            holder.residualLoan.setText(String.format("%.2f",(resultDatas[0][position - 1].getResidualLoan()                    + resultDatas[1][position - 1].getResidualLoan())));        }    }    @Override    public int getItemCount() {        return resultDatas == null ? 1 : (resultDatas[0] == null ? 1 : resultDatas[0].length + 1);    }    public class ResultDetailViewHolder extends RecyclerView.ViewHolder{        private LinearLayout itemData;        private TextView repayment;        private TextView pay;        private TextView principal;        private TextView interest;        private TextView residualLoan;        public ResultDetailViewHolder(View itemView) {            super(itemView);            itemData = (LinearLayout) itemView.findViewById(R.id.item_data);            repayment = (TextView) itemView.findViewById(R.id.repayment);            pay = (TextView) itemView.findViewById(R.id.pay);            principal = (TextView) itemView.findViewById(R.id.principal);            interest = (TextView) itemView.findViewById(R.id.interest);            residualLoan = (TextView) itemView.findViewById(R.id.residualLoan);        }    }}